int main(int argc, char *argv[]) {
    int nr_pages;
    int bytes_per_page;
    int page_cursor = 0;
    if (argc != 3)
    {
        printf("wrong arg. usage: synctest [nr_pages] [bytes_per_page]\n");
        return 0;
    }
    nr_pages = strtol(argv[1], NULL, 10);
    bytes_per_page = strtol(argv[2], NULL, 10);

    printf("synctest start\n");
    int fd = open("./synt", O_CREAT|O_RDWR);
    printf("fd %d\n", fd);
    // for (int i = 0; i < 10000; i++)
    // {
    //     write(fd, "abcdefgh", 8);
    //     fsync(fd);
    // }
    
    for (int i = 0; i < ROUND; i++)
    {
        int bpp = bytes_per_page;
        while (bpp > 0)
        {
            int w = bpp>4096?4096:bpp;
            for (int i = 0; i < nr_pages; i++)
            {
                off_t offset = 4096 *128 * i;   // page step 128
                pwrite(fd, ONE_WRITE, w, offset);
            }
            bpp -= w;
            page_cursor += w;
            if (page_cursor > w)
                page_cursor = 0;
        }
        fsync(fd);
    }
    
    write(fd, "\ndone\n", 7);
    close(fd);
    printf("synctest done\n");

    return 0;
}